#include <stdio.h>
#include <string.h>

#define HASHSIZE 26

int hash(char s){

    int hashval;
    hashval = s%HASHSIZE;
    return hashval;

}

char findTheDifference(char * s, char * t){

    int hashtable[HASHSIZE]={0};
    //create hashtable entries for s

    for(int i=0;i<strlen(s);++i){
        int hashval = hash(s[i]);
        hashtable[hashval]++;
    }

    int i;
    for (i=0;i<strlen(t);++i){
        int hashval = hash(t[i]);
        if (hashtable[hashval] > 0)
            hashtable[hashval]--;
        else
            break;
    }

    return t[i];


}


int main(int argc, char* argv[]){

	//test cases taken from leetcode
	char *s = "";
	char *t = "";

	char result = findTheDifference(s,t);
	printf("The difference is character %c\n",result);

	return 0;
}
